import { Component, OnInit } from '@angular/core';
import { LoginCredential } from '../login/login.credential';
import { LoginService } from '../login/login.service';

@Component({
  selector: 'app-edit-user-info',
  templateUrl: './edit-user-info.component.html',
  styleUrls: ['./edit-user-info.component.css']
})
export class EditUserInfoComponent implements OnInit{
  constructor(private loginService: LoginService){}

  selectedEntry : LoginCredential = new LoginCredential();

  ngOnInit(): void {
    this.selectedEntry = this.loginService.getUserList()[0];
  }

}
<head>
    <title>Bootstrap Example</title>
    <meta charset="utf-8">
    <meta name="viewport" content="width=device-width, initial-scale=1">
    <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/css/bootstrap.min.css">
    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.6.3/jquery.min.js"></script>
    <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/js/bootstrap.min.js"></script>
</head>

<div class="container">
    <h2>Edit Info : BSL</h2>
    <p>The form below contains two input elements; one of type text and one of type password:</p>
    <form>
      <div class="form-group">
        <label for="usr">Name:</label>
        <input type="text" class="form-control" id="usr" name="name" [(ngModel)] = "selectedEntry.name" >
      </div>
      <div class="form-group">
        <label for="pwd">Password:</label>
        <input type="password" class="form-control" id="pwd">
      </div>
    </form>

    <app-users> </app-users>
  </div>
